package erp.cfd;

import cfd.DElement;
import java.util.ArrayList;
import java.util.Date;

/**
 * Enables a class to provide nodes and attributes for XML of CFD 2.0, 2.2 and CFDI 3.2.
 * Interface for classroom use generated by CFDI in SIIE (i.e SDataDps CFDI from income and SData)
 * @author Juan Barajas, Sergio Flores
 */
public interface SCfdXmlCfdi32 {

    public int getCfdType();
    public String getComprobanteSerie();    // serie
    public String getComprobanteFolio();    // folio
    public Date getComprobanteFecha();      // fecha
    // sello
    public int getComprobanteFormaDePagoPagos();    // formaDePago
    // noCertificado
    // certificado
    public int getComprobanteCondicionesDePago();   // condicionesDePago
    public double getComprobanteSubtotal();         // Subtotal
    public double getComprobanteDescuento();        // Descuento
    public String getComprobanteMotivoDescuento();  // MotivoDescuento
    public double getComprobanteTipoDeCambio();     // TipoCambio
    public String getComprobanteMoneda();           // Moneda
    public double getComprobanteTotal();            // Total
    public int getComprobanteTipoDeComprobante();   // TipoDeComprobante
    public String getComprobanteMetodoDePago();     // MetodoDePago
    // LugarExpedicion
    public String getComprobanteNumCtaPago();       // NumCtaPago
    // FolioFiscalOrig
    // SerieFolioFiscalOrig
    // FechaFolioFiscalOrig
    // MontoFolioFiscalOrig
    public int getEmisorId();
    public int getEmisorSucursalId();
    public ArrayList<DElement> getElementsEmisorRegimenFiscal();
    public int getReceptorId();
    public int getReceptorSucursalId();
    public ArrayList<SCfdDataConcepto> getElementsConcepto();
    public ArrayList<SCfdDataImpuesto> getElementsImpuestos(float cfdiVersion);
    public cfd.DElement getElementComplemento();
    public cfd.DElement getElementAddenda();
}
